using System.Collections; using System.Collections.Generic; using UnityEngine;  public class HitPlaySound : MonoBehaviour {  	public AudioClip sound1; 	AudioSource audioSource;   	// Use this for initialization 	void Start () { 		audioSource = gameObject.GetComponent<AudioSource> (); 	}  	// Update is called once per frame 	void Update () { 		  	}  	void OnCollisionEnter2D (Collision2D other){ 	if(other.gameObject.tag == "CubuTag" || other.gameObject.tag == "GroundTag")  		{  			audioSource.PlayOneShot(sound1); 		} 	} }  